import timeimport bookimport zxtouchVersion=' betaV0.1'coordinate=(1965,2650)send_coordinate=(1600,100)device = zxtouch.client.zxtouch("192.168.101.9")one_book=book.book('./bookpool/book1.json')def click(device:zxtouch.client.zxtouch,x,y):    device.touch(zxtouch.touchtypes.TOUCH_DOWN,1,x,y)    device.accurate_usleep(50000)    device.touch(zxtouch.touchtypes.TOUCH_UP, 1, x,y)def open_input():    click(device,coordinate[0],coordinate[1])def click_send():    click(device,send_coordinate[0],send_coordinate[1])def send_msg(context):    device.accurate_usleep(500000)    device.insert_text(context)    device.accurate_usleep(500000)    click_send()# device.show_alert_box('skyauto'+Version,'Hello!',-1)device.accurate_usleep(1000000)# send_msg(one_book.title.encode('utf-8').decode('gbk'))# send_msg('测试abc')device.insert_text('测'.encode().decode())device.disconnect()